#!/bin/sh

vault auth enable -path=jwt jwt

vault write auth/jwt/config jwks_url="http://secrets-vault:3456/.well-known/keys"
vault write auth/jwt/role/wasmcloud-role @docker-entrypoint-initvault.d/wasmcloud-role.json

vault policy write wasmcloud-role-policy - << EOF
path "secret/*" {
  capabilities = ["create", "update", "read"]
}
EOF

# As this is only used for development, and everything is ephemeral, we can store it as plaintext.

vault kv put secret/nats/auth/callout jwt="eyJ0eXAiOiJKV1QiLCJhbGciOiJlZDI1NTE5LW5rZXkifQ..weoQo4WJpaSt33wEFe9Nv37d8TNqK-k6VkQgiGe3UbZlWi990SwYgOk9J9mwwlei6vK2eKogOInxY2PLwgrEDg" seed="SUADJWVMIVBGVBRGAAVQDLPAQQREXNXRVCHLJ22UW3J6JL3LX2CIM3CRHA"

